import React, { useState, useEffect } from "react";

function App() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [isNextButton, setIsNextButton] = useState(false);
  const [isResultButton, setIsResultButton] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState();
  const [selectedAnswers, setSelectedAnswers] = useState([]);
  const [time, setTime] = useState(30);
  const questions = [
    {
      question: "I am a first question",
      answers: ["answer 1", "answer 2", "answer 3", "answer 4"],
      trueAnswer: "answer 2",
    },
    {
      question: "I am a second question",
      answers: ["answer 21", "answer 22", "answer 23", "answer 24"],
      trueAnswer: "answer 23",
    },
    {
      question: "I am a third question",
      answers: ["answer 31", "answer 32", "answer 33", "answer 34"],
      trueAnswer: "answer 34",
    },
    {
      question: "I am a fourth question",
      answers: ["answer 41", "answer 42", "answer 43", "answer 44"],
      trueAnswer: "answer 41",
    },
  ];

  const selectAnswer = (selected, index) => {
    if (currentQuestion === questions.length - 1) {
      setIsNextButton(false);
      setIsResultButton(true);
    } else {
      setIsNextButton(true);
    }
    setSelectedIndex(index);
  };

  const nextQuestion = (index) => {
    setIsNextButton(false);
    if (currentQuestion + 1 >= questions.length) {
      console.log("sorular bitti");
    } else {
      const selectedAnswer = questions[currentQuestion].answers[index];
      const newAnswers = [...selectedAnswers, selectedAnswer];
      setSelectedAnswers(newAnswers);
      setCurrentQuestion(currentQuestion + 1);
      setSelectedIndex();
    }
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setTime(time - 1);
    },1000)

    return () => clearInterval(timer)
  })
     setInterval(() => {
      setTime(time - 1);
    }, 1000);

  return (
    <div className="app-container">
      <div className="quiz-box">
        <div className="progress-box">
          <div className="progress-top">
            <div className="progress-texts">
              <h2 className="progress-title">Quiz Progress</h2>
              <p className="prgress-description">Track your quiz progress</p>
            </div>
            <div className="progress-icon">
              <i className="bi bi-bar-chart"></i>
            </div>
          </div>
          <div className="progress-bottom">
            <div
              className="progress-circle"
              aria-valuemin="0"
              aria-valuemax="100"
              style={{
                "--value": ((currentQuestion + 1) / questions.length) * 100,
              }}
            >
              <span className="progress-big">{currentQuestion + 1}</span>
              <span className="progress-mini">/{questions.length}</span>
            </div>
            <p className="progress-detail">
              You solve the {currentQuestion + 1}. question out of a total of{" "}
              {questions.length} questions
            </p>
          </div>
        </div>
        <div className="question-box">
          <div className="question-text">
            <h2 className="question-title">Question: {currentQuestion + 1}</h2>
            <h3 className="question">{questions[currentQuestion].question}</h3>
          </div>
          <div
            className="progress-circle time"
            aria-valuemin="0"
            aria-valuemax="100"
            style={{ "--value": 30 }}
          >
            <span className="time">{time}</span>
          </div>
        </div>

        <div className="answers-boxes">
          {questions[currentQuestion].answers.map((answer, index) => {
            return (
              <label
                onClick={() => selectAnswer(answer, index)}
                key={index}
                htmlFor={index}
                className={
                  selectedIndex === index
                    ? "answer-label selected"
                    : "answer-label"
                }
              >
                {answer}
                <input type="radio" name="answer" id={index} />
              </label>
            );
          })}
        </div>

        {isNextButton ? (
          <div className="next">
            <button
              onClick={() => nextQuestion(selectedIndex)}
              type="button"
              className="next-btn"
            >
              Next Question
              <div className="icon">
                <i className="bi bi-arrow-right"></i>
              </div>
            </button>
          </div>
        ) : null}

        {isResultButton ? (
          <div className="next">
            <button
              onClick={() => nextQuestion(selectedIndex)}
              type="button"
              className="next-btn result-btn"
            >
              See Results
              <div className="icon">
                <i className="bi bi-bar-chart"></i>
              </div>
            </button>
          </div>
        ) : null}

        <div className="message">
          <div className="icon">
            <i className="bi bi-exclamation-triangle"></i>
          </div>
          <span>You must choose an answer!</span>
        </div>
        <div className="message animation">
          <div className="icon">
            <i className="bi bi-exclamation-triangle"></i>
          </div>
          <span>You must hurry up!</span>
        </div>
      </div>
    </div>
  );
}

export default App;
